mapper.sql:
		WHERE 1=1
  	AND id = #{id}
  	AND deptid = #{deptid}
  	AND cuscode = #{cuscode}
  	AND htcode = #{htcode}
  	AND rent_fee = #{rentFee}
  	AND repair_fee = #{repairFee}
  	AND redress_fee = #{redressFee}
  	AND transport_fee = #{transportFee}
  	AND zx_fee = #{zxFee}
  	AND other_fee = #{otherFee}
  	AND stop_fee = #{stopFee}
  	AND total_fee = #{totalFee}
  	AND due_fee = #{dueFee}
  	AND received_fee = #{receivedFee}
  	AND settle_month = #{settleMonth}
  	AND creattime = #{creattime}

UPDATE
deptid = #{deptid}, cuscode = #{cuscode}, htcode = #{htcode}, rent_fee = #{rentFee}, repair_fee = #{repairFee}, redress_fee = #{redressFee}, transport_fee = #{transportFee}, zx_fee = #{zxFee}, other_fee = #{otherFee}, stop_fee = #{stopFee}, total_fee = #{totalFee}, due_fee = #{dueFee}, received_fee = #{receivedFee}, settle_month = #{settleMonth}, creattime = #{creattime}
WHERE id = #{id}

update.foreach:
				UPDATE 


		  	   rent_fee = (#{item.rentFee}+rent_fee),
		  	   transport_fee = (#{item.transportFee}),
		  	   zx_fee = (#{item.zxFee}),
		  	   repair_fee = (#{item.repairFee}),
		  	   other_fee = (#{item.otherFee}),
		  	   due_fee=(#{item.rentFee}+rent_fee+#{item.transportFee}+#{item.zxFee}+#{item.repairFee}+#{item.otherFee})

               WHERE htcode = #{item.htcode} 
		       and deptid = #{item.deptid}
		       and settle_month = #{item.settleMonth}
